package LabPrograms;

public class whileloop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
           int num=1,sum=0;
           while(num<=10)
           {
        	   sum=sum+num;
        	   num++;
        	  
        	   System.out.println(" numbers are:"+num);
        	   System.out.println("sum of numbers:"+sum);
           }
         
	}

}
